import axios from "axios";




export function shortLink(Link){
    return async function (dispatch){
        try{
            const json = await axios.post(`/shortLink`, Link);

                return dispatch({
                type: "LOGIN_STATUS",
                payload: json.data.status})
            }
        catch(err){
            return dispatch({
            type: "LOGIN_STATUS",
            payload: "error"})
        }
        
    }
  }
