import axios from "axios";




export function shortLink(Link){
    return async function (dispatch){
        try{
            const json = await axios.post(`/shortLink`, Link);

                return dispatch({
                type: "LOGIN_STATUS",
                payload: json.data.status})
            }
        catch(err){
            return dispatch({
            type: "LOGIN_STATUS",
            payload: "error"})
        }
        
    }
  }

export function validateLink(Link){
    return async function (dispatch){
        try{
            const response = await axios.get(`http//$Link`);
            console.log(response.status)  
            return dispatch({
                type: "URL_STATUS",
                payload: response.status})
            }
        catch(err){
          return dispatch({
            type: "LOGIN_STATUS",
            payload: "error"})
        }
        
    }
}


