const initialState = {
    li:''
}

const rootReducer = (state = initialState, action) => {
    const { type, payload } = action;
    switch (action.type) {
        case "LINK":
            return{
                ...state,
                validate: action.payload
            }
            default:
                return state;
            }
        }
        
        export default rootReducer;
